<template>
  <div class="login-container">
    <!--    <div style="width: 1912px;height: 932px;overflow-x: scroll">-->
    <div class="logo">
      <!--      src="data:image/svg+xml;base64,"-->

      <img :src="logoLogin" alt="YOZO" style="
        width: 25%;
        height: 25%;
          top: 0px;
          position: fixed;
          left: 5%;">
    </div>

    <div class="login-form">
      <el-form :model="loginForm" autocomplete="on" :rules="loginRules" ref="loginForm" label-position="left"
               label-width="0px" class="card-box login-form">
        <h3 class="title">乡创电商运营平台</h3>
        <!-- 2024.04.19-lz-add 给每个formItem新增class属性 -->
        <el-form-item prop="username" class="formItem">
          <span class="svg-container svg-container_login">
            <svg-icon icon-class="user" />
          </span>
          <el-input class="inputItem" v-model="loginForm.username" autocomplete="on" placeholder="请输入用户名" />
        </el-form-item>
        <el-form-item class="test formItem" prop="password">
          <span class="svg-container">
            <svg-icon icon-class="password"></svg-icon>
          </span>
          <el-input class="inputItem" type="password" @keyup.enter.native="handleLogin" autocomplete="new-password"
                    v-model="loginForm.password" placeholder="请输入密码">
          </el-input>
        </el-form-item>
        <el-form-item class="formItem">
          <el-button type="primary" style="width:100%;" :loading="loading" @click.native.prevent="handleLogin">
            登录
          </el-button>
        </el-form-item>
      </el-form>

    </div>

    <div class="copyright">
      <li>Copyright © 2025 BISTU 大庄科乡. All rights reserved.</li>
    </div>
    <!--    </div>-->

  </div>
</template>
<script>
import logoLogin from '@/assets/web/logoEC.png'
// import logoLogin from '@/assets/web/webOFDLogo.svg'
import MD5 from "js-md5";
import {encrypt} from "../../utils/EncryptionUtil";
export default {

  name: 'login',
  data() {
    return {
      logoLogin,
      loginForm: {
        username: '',
        password: ''
      },
      loginRules: {
        username: [{ required: true, trigger: 'blur', message: "请输入用户名" }],
        password: [{ required: true, trigger: 'blur', message: "请输入密码" }]
      },
      loading: false
    }
  },
  methods: {
    handleLogin() {
      this.$refs.loginForm.validate(valid => {
        if (valid) {
          this.loading = true;
          // 加密密码
          const rawPassword = this.loginForm.password;
          // const hashedPassword = MD5(rawPassword);
          const hashedPassword = encrypt(rawPassword);
          this.loginForm.password = hashedPassword;
          let sendForm = {}
          sendForm.password = hashedPassword;
          // sendForm.password = this.loginForm.password;
          sendForm.username = this.loginForm.username;
          // this.$store.dispatch('Login', this.loginForm).then(data => {
          this.$store.dispatch('Login', sendForm).then(data => {
            this.loading = false
            this.$router.push({ path: '/' })
          }).catch(() => {
            this.loading = false
          })
        } else {
          return false
        }
      })
    }
  }
}
</script>
